mapper.select:
SELECT B.* FROM ( SELECT ROWNUM RNUM, A.* FROM ( SELECT BO_NO, BO_TITLE, BO_WRITER , BO_IP, BO_MAIL, BO_PASS , BO_CONTENT, BO_DATE, BO_HIT , ( SELECT COUNT(*) FROM ATCH_FILE WHERE BO_ATCH = ATCH_ID ) ATCH_COUNT FROM FREEBOARD
ORDER BY BO_NO DESC ) A ) B
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.insert:

