	, #{boAtch,jdbcType=NUMERIC}
)

mapper.update:
UPDATE FREEBOARD
SET
	BO_MAIL = #{boMail,jdbcType=VARCHAR}
	, BO_CONTENT = #{boContent,jdbcType=CLOB}
	, BO_ATCH = #{boAtch,jdbcType=NUMERIC}
